script:
frappe.ui.form.on('Employee', {
    custom_certificate_expiry_date: function(frm) {
        if (frm.doc.custom_certificate_expiry_date) {
            const today = frappe.datetime.get_today();
            const diff = frappe.datetime.get_diff(frm.doc.custom_certificate_expiry_date, today);
            frm.set_value('custom_days_remaining', diff);
        } else {
            frm.set_value('custom_days_remaining', null);
        }
    },

    onload: function(frm) {
        if (frm.doc.custom_certificate_expiry_date) {
            const today = frappe.datetime.get_today();
            const diff = frappe.datetime.get_diff(frm.doc.custom_certificate_expiry_date, today);
            frm.set_value('custom_days_remaining', diff);
        }
    }
});
